import React, { Component } from 'react'

class Form extends Component {
    constructor(props) {
        super(props)

        this.state = {
             username:" ",
             email:" ",
             topic:"react"
        }
    }

    changeHandler=(event)=>{
        this.setState({
            username:event.target.value

        })
    }
    changeHandlerEmail=(event)=>{
        this.setState({
            email:event.target.value

        })
    }
    changeHandlerTopic=(event)=>{
        this.setState({
            topic:event.target.value

        })


    }

    changeSubmit=event=>{
        alert(`${this.state.username}${this.state.email}${this.state.to}`)
    }

    render() {
        return (
           <form onSubmit={this.changeSubmit}>
               <div>
                <label>Username</label>
                <input type='text'value={this.state.username} onChange={this.changeHandler}></input>
                </div>
                <div>
                    <label>Email</label>
                    <input type='text'value={this.state.email} onChange={this.changeHandlerEmail}></input>
                </div>
                <div>
                    <label>Topic</label>
                    <select value={this.state.topic}onChange={this.changeHandlerTopic}>
                    <option value="react">React</option>
                    <option value="angular">Angular</option>
                    <option value="vue">Vue</option>
                    </select>
                    <button type="submit">submit</button>


                </div>

           </form>
        )
    }
}

export default Form
