import React, { Component } from 'react'

class Form extends Component {
    constructor(props) {
        super(props)

        this.state = {
             username:" "
        }
    }

    changeHandler=(event)=>{
        this.setState({
            username:event.target.value

        })
    }
    changeHandlerEmail=(event)=>{
        this.setState({
            username:event.target.value

        })
    }

    render() {
        return (
           <form>
               <div>
                <label>Username</label>
                <input type='text'value={this.state.username} onChange={this.changeHandler}></input>
                </div>
                <div>
                    <label>Email</label>
                    <textarea value={this.state.email} onChange={this.changeHandlerEmail}></textarea>
                </div>

           </form>
        )
    }
}

export default Form
