import React, { Component } from "react";
import './Dashboard.css';
import { getAllUser, getAllMessages } from "../services/userServices";
import socketApi from '../components/socketApi';
import openSocket from "socket.io-client";
const socket = openSocket("http://localhost:7000");

 export default class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userArray: [],
      messageArray: [],
      // messages: "",
      // ReceiverId:"",
      // ReceiverName:""

    };
  }

  // setRedirect = () => {
  //   this.setState({
  //     redirect: true
  //   })

  // }


  // renderRedirect = () => {
  //   if (this.state.redirect) {
  //       var path = '/'
  //       this.props.history.push(path)
  //   }
  // }

  componentDidMount() {
    this.getUser();
    this.getUserMessages();




      //================================================================
      socketApi.receivedMsg((error, result) => {

        if (result) {

            console.log("result is back...", result);



            var resultArray = [];

            resultArray = this.state.Messages;

            resultArray.push(result)

            this.setState({
              messages: resultArray
            })


            console.log("Received Messages are---->", JSON.stringify(this.state.Messages));


        }
        else {
            console.log("Error in received message--->", error);

        }
    })

  }

  getUser() {
    getAllUser().then(res => {
      console.log(" response ", res.data.data);
      var users = res.data.data;
      console.log("users list", users);
      this.setState({ userArray: users });
      console.log("in state", this.state);
    });
  }

  person = key => {
    console.log("on click", key);
    localStorage.setItem("receiverName", key.firstName);
    localStorage.setItem("receiverLastName", key.lastName);
    localStorage.setItem("receiverid", key._id);
    localStorage.setItem("receiveremail", key.email);
    this.getUserMessages();
  };

  getUserMessages() {
    getAllMessages()
      .then(res => {
        console.log("all messges", res.data.data);
        var messages = res.data.data;
        console.log(" user messages", messages);
        this.setState({ messageArray: messages });
        console.log("in message states", this.state);
      })

      .catch(err => {
        console.log(" error ", err);
      });
  }

  getReceiverId() {
    console.log("send botton clicked..");
    let receiverId = localStorage.getItem('receiverid');
    let receiver_name = localStorage.getItem('receiverName');
    console.log("receiver name", receiver_name);
    console.log("receiverID", receiverId);
    let senderemail=localStorage.getItem('senderEmail')
    let senderid= localStorage.getItem('senderId');
    console.log("sendermail..",senderemail);
    console.log("senderId..",senderid);


  }

  render() {
    const printuser = this.state.userArray.map(key => {
      return <div onClick={() => this.person(key)}>{key.firstName}</div>;
    });
    console.log(printuser);
    const messages=this.state.messageArray.map(msg=>{

      return <div>{msg.message}</div>
    })

    return (
      <div className="mainchatdiv">
        <div id="appBar">
          <p className="p">Chat Application</p>
          <button className="LogOutButton"onClick={() => this.logOutClick()}>Log Out</button>
        </div>
        <div className="chatApp">
          <div className="list-username">
            <div className="users">Username</div>
            <div className="List">
              List
              {printuser}
            </div>
          </div>
          <div className="chat-box-width">
            <div className="ChatBox">chat box

            {messages}
            </div>
            <div className="inputStyle">
              <input className="input" placeholder=" type here... "></input>
              <button
                className="sendButton"
                value={this.state.messageArray.id}
                onClick={this.getReceiverId}
              >
                send
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
